        <div class="minder-content">
            <template v-if="runningPlugins.includes('minder')">
                <IFrame ref="frame" class="minder-iframe" :src="url" @on-message="onMessage"/>
                <div v-if="loadIng" class="minder-loading"><Loading/></div>
