<script>
import { mapState } from "vuex";
import IFrame from "../pages/manage/components/IFrame.vue";
    export default {
        name: 'mind-editor',
        components: {IFrame},
        props: {
            value: {
                default: function () {
                    return {}
                }
            },
            readOnly: {
                type: Boolean,
                default: false
            },
        },
        data() {
            return {
                loadIng: true,
                isHand: this.readOnly,
                zoom: 100,

                backup: null
            };
        },
        mounted() {
            window.addEventListener('message', this.handleMessage)
            // 加载插件信息
            this.$store.dispatch('loadRunningPlugins')
        },
        beforeDestroy() {
            window.removeEventListener('message', this.handleMessage)
        },
        methods: {
            onMessage(data) {
                if (data.app !== 'minder' || !this.runningPlugins.includes('minder')) {
                    return
                }
                switch (data.action) {
                    case 'ready':
                        this.loadIng = false
                        this.rendData()
                        break;

                    case 'content':
                        this.backup = JSON.stringify(data.content)
                        this.$emit('input', data.content);
                        break;

                    case "save":
                        this.$emit('saveData');
                        break;
                }
            },

            onHand() {
                if (this.readOnly) {
                    this.execCommand('removeAllSelected')
                    return
                }
                this.execCommand('Hand')
                this.isHand = !this.isHand
            },

            execCommand(command, value) {
                this.$refs.frame.postMessage({
                    app: 'minder',
                    action: 'command',
                    command,
                    value
                })
                if (command === 'goCenter' || command === 'removeAllSelected') {
                    return
                }
                setTimeout(_ => {
                    if (this.isHand) {
                        this.$refs.frame.postMessage({
                            app: 'minder',
                            action: 'commandAlways',
                            command: 'Hand',
                            value
                        })
                    }
                }, 300)
            },

            exportHandle(type, filename) {
                this.$refs.frame.postMessage({
                    app: 'minder',
                    action: 'export',
                    type,
                    name: filename || (this.value.root.data.text || this.$L('无标题'))
                })
            },

            rendData() {
                if (this.loadIng) {
                    return
                }
                if (this.backup === JSON.stringify(this.value)) {
                    return
                }
                this.$refs.frame.postMessage({
                    app: 'minder',
                    action: 'setContent',
                    content: this.value
                })
            }
        },
        computed: {
            ...mapState(['runningPlugins']),
            url() {
                return $A.mainUrl(`minder/index.html?type=manual&readonly=${this.readOnly ? 'yes' : 'no'}`)
            }
        },
        watch: {
            value: {
                handler: function (json) {
                    if (typeof json !== "object" || json === null) {
                        json = {
                            root: json,
                            theme: "fresh-blue",
                            template: "default",
                        };
                    }
                    if (typeof json.root !== "object" || json.root === null || json.root.length == 0) {
                        json.root = {
                            data: {
                                id: $A.randomString(12),
                                text: this.$L('默认节点'),
                            },
                            children: []
                        }
                    }
                    if (typeof json.theme !== "string") {
                        json.theme = "fresh-blue";
                    }
                    if (typeof json.template !== "string") {
                        json.template = "default";
                    }
                    this.rendData();
                },
                deep: true,
                immediate: true
            },

            zoom(val) {
                this.execCommand('Zoom', val)
            }
        }
    };
</script>
